import React, { useState } from "react";
import ToDoItem from "../toDoItem/toDoItem";
import "./style.css";

function Todo() {
  const [state, setState] = useState({
    todos: [
      { id: 1, name: "play" },
      { id: 2, name: "study" },
      { id: 3, name: "code" },
    ],
  });

  const submitHandler = (e) => {
    e.preventDefault();
    let inp = e.target.task.value;
    if (inp) {
      let newObj = { id: state.todos.length + 1, name: inp };
      setState({ todos: [...state.todos, newObj] });
    }
  };

  const handleDelete = (i) => {
    let filtered = state.todos.filter((item) => item.id !== i);
    setState({ todos: filtered });
  };

  const handleEdit = (i) => {
    let newValue = prompt("Edit this task:");
    let mine = state.todos.map((l) => {
      return (l.name = newValue);
    });
    console.log(mine);
    setState({ todos: [{id:3, name:"SSSS"}] });
  };

  return (
    <div className="todo">
      <form onSubmit={submitHandler}>
        <input type="text" name="task" />
        <button>Add Task</button>
      </form>
      <h1>To Do List:</h1>
      <ul>
        {state.todos.map((todo, i) => (
          <ToDoItem
            task={todo}
            handleDelete={handleDelete}
            handleEdit={handleEdit}
            key={i}
          />
        ))}
      </ul>
    </div>
  );
}

export default Todo;
